	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileStream("Chap0614.pdf", FileMode.Create));
	// step 3
	document.open();
	// step 4
	Image img = Image.getInstance("pngnow.png");
	img.scalePercent(70);
	Chunk ck = new Chunk(img, 0, -5);
	Table table = new Table(3);
	table.setWidth(100);
	table.setPadding(2);
	table.setDefaultHorizontalAlignment(ElementConst.ALIGN_CENTER);
	Cell cell = new Cell(new Chunk(img, 0, -13));
	cell.setBackgroundColor(GDI.Color.LightGray);
	cell.setHorizontalAlignment(ElementConst.ALIGN_CENTER);
	table.addCell("I see an image\non my right");
	table.addCell(cell);
	table.addCell("I see an image\non my left");
	table.addCell(cell);
	table.addCell("I see images\neverywhere");
	table.addCell(cell);
	table.addCell("I see an image\non my right");
	table.addCell(cell);
	table.addCell("I see an image\non my left");

	Phrase p1 = new Phrase("This is an image ");
	p1.add(ck);
	p1.add(" just here.");
	document.add(p1);
	document.add(p1);
	document.add(p1);
	document.add(p1);
	document.add(p1);
	document.add(p1);
	document.add(p1);
	document.add(table);

	// step 5
	document.close();
}

