 		// step 4
 		Paragraph paragraph = new Paragraph("Please visit my ");
 		Anchor anchor1 = new Anchor("website (external reference)", FontFactory.getFont(FontFactory.HELVETICA, 12, Font.UNDERLINE, GDI.Color.Blue));
 		anchor1.setReference("http
 		anchor1.setName("top");
 		paragraph.add(anchor1);
 		paragraph.add(new Chunk(".


































"));
 		document.add(paragraph);
 		Anchor anchor2 = new Anchor("please jump to a local destination", FontFactory.getFont(FontFactory.HELVETICA, 12, Font.NORMAL, GDI.Color.Blue));
 		anchor2.setReference("#top");
 		document.add(anchor2);

 		// step 5
 		document.close();
 	}

