clone_pair.clone_fragment:
	{
        cb.setColorStroke(GDI.Color.Black);
        cb.setColorFill(GDI.Color.Gray);
        cb.rectangle(x, y, 100, 200);
        cb.fill();
        cb.setLineWidth(2);
        cb.rectangle(x, y, 200, 200);
        cb.stroke();
    }


    public static void pictureCircles(float x, float y, PdfContentByte cb)
	{
        cb.setColorFill(GDI.Color.Red);
        cb.circle(x + 70, y + 70, 50);
        cb.fill();
        cb.setColorFill(GDI.Color.Yellow);
        cb.circle(x + 100, y + 130, 50);
        cb.fill();
        cb.setColorFill(GDI.Color.Blue);
        cb.circle(x + 130, y + 70, 50);
        cb.fill();
    }

	{
        cb.setColorStroke(GDI.Color.Black);
        cb.setColorFill(GDI.Color.Gray);
        cb.rectangle(x, y, 100, 200);
        cb.fill();
        cb.setLineWidth(2);
        cb.rectangle(x, y, 200, 200);
        cb.stroke();
    }


// we override the onCloseDocument method
public override void onCloseDocument(PdfWriter writer, Document document) 
{
	template.beginText();
	template.setFontAndSize(bf, 8);
	template.showText((writer.getPageNumber() - 1).ToString());
	template.endText();
}


    public static void pictureCircles(float x, float y, PdfContentByte cb)
	{
        cb.setColorFill(GDI.Color.Red);
        cb.circle(x + 70, y + 70, 50);
        cb.fill();
        cb.setColorFill(GDI.Color.Yellow);
        cb.circle(x + 100, y + 130, 50);
        cb.fill();
        cb.setColorFill(GDI.Color.Blue);
        cb.circle(x + 130, y + 70, 50);
        cb.fill();
    }


private void AddRadioKid(String caption, String onState, String state, PdfFormField radio, PdfAppearance tpOn, PdfAppearance tpOff)
{
	PdfFormField radio1 = PdfFormField.createEmpty(writer);
	radio1.setFlags(flags);
	radio1.setWidget(new Rectangle(PosX, PosY, PosX + Height, PosY + Height), PdfAnnotation.HIGHLIGHT_INVERT);
	radio1.setAppearanceState(state);
	radio1.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, "Off", tpOff);
	radio1.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, onState, tpOn);
	radio.addKid(radio1);
	cb.beginText();
	cb.setTextMatrix(PosX + Height*2, PosY + (Height-fontSize)/2 + (Height/2)*0.1f);
	cb.showText(caption);
	cb.endText();
}

{
	float[] widths = width[0];
	PdfContentByte cb = canvases[PdfPTable.TEXTCANVAS];
	cb.saveState();
	cb.setLineWidth(2);
	cb.setRGBColorStroke(255, 0, 0);
	cb.rectangle(widths[0], heights[heights.Length - 1], widths[widths.Length - 1] - widths[0], heights[0] - heights[heights.Length - 1]);
	cb.stroke();
	if (headerRows > 0) 
	{
		float headerHeight = heights[0];
		for (int k = 0; k < headerRows; ++k)
			headerHeight += heights[k];
		for (int k = 0; k < headerRows; ++k)
			headerHeight += heights[k];
		cb.setRGBColorStroke(0, 0, 255);
		cb.rectangle(widths[0], heights[headerRows], widths[widths.Length - 1] - widths[0], heights[0] - heights[headerRows]);
		cb.stroke();
	}
	cb.restoreState();
	cb = canvases[PdfPTable.BASECANVAS];
	cb.saveState();
	cb.setLineWidth(.5f);
	for (int line = 0; line < heights.Length - 1; ++line) 
	{

		widths = width[line];
		for (int col = 0; col < widths.Length - 1; ++col) 
		{
			if (line == 0 && col == 0)
				cb.setAction(new PdfAction("http
					widths[col], heights[line + 1], widths[col + 1], heights[line]);
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col + 1], heights[line]);
			cb.stroke();
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col], heights[line + 1]);
			cb.stroke();
		}
		for (int col = 0; col < widths.Length - 1; ++col) 
		{
			if (line == 0 && col == 0)
				cb.setAction(new PdfAction("http
					widths[col], heights[line + 1], widths[col + 1], heights[line]);
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col + 1], heights[line]);
			cb.stroke();
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col], heights[line + 1]);
			cb.stroke();
		}
	}
	for (int line = 0; line < heights.Length - 1; ++line) 
	{

		widths = width[line];
		for (int col = 0; col < widths.Length - 1; ++col) 
		{
			if (line == 0 && col == 0)
				cb.setAction(new PdfAction("http
					widths[col], heights[line + 1], widths[col + 1], heights[line]);
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col + 1], heights[line]);
			cb.stroke();
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col], heights[line + 1]);
			cb.stroke();
		}
	}
	cb.restoreState();
}

