public class Chap1202 
{

	public static void Main(String[] args) 
	{

		Console.WriteLine("Chapter 12 example 2

		// step 1
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);
		try 
		{
			// step 2
			PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap1202.pdf", FileMode.Create));
			// step 3
			document.open();
			// step 4
			BaseFont bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.WINANSI, BaseFont.NOT_EMBEDDED);
			// table 1
			PdfPTable table = new PdfPTable(4);
			table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
			for (int k = 0; k < 24; ++k) 
			{
				if (k != 0)
					table.addCell("" + k);
				else
					table.addCell("This is an URL");
			}
			for (int k = 0; k < 24; ++k) 
			{
				if (k != 0)
					table.addCell("" + k);
				else
					table.addCell("This is an URL");
			}
			MyTableEvent aEvent = new MyTableEvent();
			table.setTableEvent(aEvent);
			table.setTotalWidth(300);
			// write table 1 at some position
			table.writeSelectedRows(0, -1, 100, 600, writer.getDirectContent());
			// add table 1 (default position)
			document.add(table);
			document.newPage();
			// table 2
			table = new PdfPTable(4);
			float fontSize = 12;
			table.getDefaultCell().setPaddingTop(bf.getFontDescriptor(BaseFont.ASCENT, fontSize) - fontSize + 2);
			table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
			for (int k = 0; k < 500 * 4; ++k) 
			{
				if (k == 0)
					table.addCell(new Phrase("This is an URL", new Font(bf, fontSize)));
				else
					table.addCell(new Phrase("" + k, new Font(bf, fontSize)));
			}
			for (int k = 0; k < 500 * 4; ++k) 
			{
				if (k == 0)
					table.addCell(new Phrase("This is an URL", new Font(bf, fontSize)));
				else
					table.addCell(new Phrase("" + k, new Font(bf, fontSize)));
			}
			table.setTableEvent(aEvent);
			table.setHeaderRows(3);
			document.add(table);
		}
		catch (Exception de) 
		{
			Console.WriteLine(de.StackTrace + "\n" + de.ToString());
		}
		// step 5
		document.close();
	}

{
	Console.WriteLine("Chapter 10 example 10

	// step 1
	Document document = new Document(PageSize.A4, 50, 50, 50, 50);
	// step 2
	PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap1010.pdf", FileMode.Create));
	// step 3
	document.open();
	// step 4
	PdfPTable table = new PdfPTable(4);
	PdfPTable nested1 = new PdfPTable(2);
	nested1.addCell("1.1");
	nested1.addCell("1.2");
	PdfPTable nested2 = new PdfPTable(1);
	nested2.addCell("2.1");
	nested2.addCell("2.2");
	for (int k = 0; k < 24; ++k) 
	{
		if (k == 1) 
		{
			table.addCell(nested1);
		}
		else if (k == 20) 
		{
			table.addCell(nested2);
		}
		else 
		{
			table.addCell("cell " + k);
		}
	}
	for (int k = 0; k < 24; ++k) 
	{
		if (k == 1) 
		{
			table.addCell(nested1);
		}
		else if (k == 20) 
		{
			table.addCell(nested2);
		}
		else 
		{
			table.addCell("cell " + k);
		}
	}
	table.setTotalWidth(300);
	table.writeSelectedRows(0, -1, 100, 600, writer.getDirectContent());
	// step 5
	document.close();
}


	public void tableLayout(PdfPTable table, float[][] width, float[] heights, int headerRows, int rowStart, PdfContentByte[] canvases) 
	{
