		PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap1013.pdf", FileMode.Create));
		BaseFont bf = BaseFont.createFont("Helvetica", "winansi", BaseFont.NOT_EMBEDDED);

		// step 3
		document.open();

		// step 4
