			cb.beginText();
			cb.setFontAndSize(bf, 12);
			cb.setTextMatrix(280, 40);
			cb.showText(text);
			cb.endText();
			cb.addTemplate(template, 280 + len, 40);
			document.newPage();
		}
		template.beginText();
		template.setFontAndSize(bf, 12);
		template.showText((writer.getPageNumber() - 1).ToString());
		template.endText();

		// step 5
		document.close();
	}

