{
	Console.WriteLine("Chapter 10 example 5

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap1005.pdf", FileMode.Create));

	// step 3
	document.open();

	// step 4

	// we create some content
	BaseFont bf = BaseFont.createFont(BaseFont.COURIER, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
	Font font = new Font(bf, 11, Font.NORMAL);

	Phrase unicodes = new Phrase(15, "UNI\n", font);
	Phrase characters = new Phrase(15, "\n", font);
	Phrase names = new Phrase(15, "NAME\n", font);

	for (int i = 0; i < 27; i++) 
	{
		unicodes.add(uni[i] + "\n");
		characters.add(code[i] + "\n");
		names.add(name[i] + "\n");
	}
	for (int i = 0; i < 27; i++) 
	{
		unicodes.add(uni[i] + "\n");
		characters.add(code[i] + "\n");
		names.add(name[i] + "\n");
	}

	// we grab the ContentByte and do some stuff with it
	PdfContentByte cb = writer.getDirectContent();

	ColumnText ct = new ColumnText(cb);
	ct.setSimpleColumn(unicodes, 60, 300, 100, 300 + 28 * 15, 15, ElementConst.ALIGN_CENTER);
	ct.go();
	cb.rectangle(103, 295, 52, 8 + 28 * 15);
	cb.stroke();
	ct.setSimpleColumn(characters, 105, 300, 150, 300 + 28 * 15, 15, ElementConst.ALIGN_RIGHT);
	ct.go();
	ct.setSimpleColumn(names, 160, 300, 500, 300 + 28 * 15, 15, ElementConst.ALIGN_LEFT);
	ct.go();

	// step 5
	document.close();
}

{
	Console.WriteLine("Chapter 2 example 1

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileStream("Chap0201.pdf", FileMode.Create));

	// step 3
	document.open();

	// step 4
	Font[] fonts = new Font[14];
	int i = 0;
	fonts[i++] = FontFactory.getFont(FontFactory.COURIER, 12, Font.NORMAL);
	fonts[i++] = FontFactory.getFont(FontFactory.COURIER, 12, Font.BOLD);
	fonts[i++] = FontFactory.getFont(FontFactory.COURIER, 12, Font.ITALIC);
	fonts[i++] = FontFactory.getFont(FontFactory.COURIER, 12, Font.BOLD | Font.ITALIC);
	fonts[i++] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.NORMAL);
	fonts[i++] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.BOLD);
	fonts[i++] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.ITALIC);
	fonts[i++] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.BOLD | Font.ITALIC);
	fonts[i++] = FontFactory.getFont(FontFactory.TIMES_ROMAN, 12, Font.NORMAL);
	fonts[i++] = FontFactory.getFont(FontFactory.TIMES_ROMAN, 12, Font.BOLD);
	fonts[i++] = FontFactory.getFont(FontFactory.TIMES_ROMAN, 12, Font.ITALIC);
	fonts[i++] = FontFactory.getFont(FontFactory.TIMES_ROMAN, 12, Font.BOLD | Font.ITALIC);
	fonts[i++] = FontFactory.getFont(FontFactory.SYMBOL, 12, Font.NORMAL);
	fonts[i++] = FontFactory.getFont(FontFactory.ZAPFDINGBATS, 12, Font.NORMAL);
	for (i = 0; i < fonts.Length; i++) 
	{
		Chunk chunk = new Chunk("This is some", fonts[i]);
		document.add(new Phrase(chunk));
		document.add(new Phrase(new Chunk(" font. ",
			fonts[i]).setTextRise((i % 2 == 0) ? -6 
	}
	for (i = 0; i < fonts.Length; i++) 
	{
		Chunk chunk = new Chunk("This is some", fonts[i]);
		document.add(new Phrase(chunk));
		document.add(new Phrase(new Chunk(" font. ",
			fonts[i]).setTextRise((i % 2 == 0) ? -6 
	}
	document.add(new Phrase(new Chunk("This text is underlined",
		FontFactory.getFont(FontFactory.HELVETICA, 12, Font.UNDERLINE))));
	document.add(new Phrase(new Chunk("This font is of type ITALIC | STRIKETHRU",
		FontFactory.getFont(FontFactory.HELVETICA, 12, Font.ITALIC | Font.STRIKETHRU))));
	Chunk ck = new Chunk("This text has a yellow background color", FontFactory.getFont(FontFactory.HELVETICA, 12));
	ck.setBackground(GDI.Color.Yellow);
	document.add(new Phrase(ck));

	// step 5
	document.close();
}


	// we override only the onGenericTag method
	public override void onGenericTag(PdfWriter writer, Document document, Rectangle rect, String text) 
	{
		glossary[text] = writer.getPageNumber();
	}

	// we add a method to retrieve the glossary
	public SortedList getGlossary() 
	{
		return glossary;
	}
}

public class Chap0209 
{
	public static void Main(String[] args) 
	{
		Console.WriteLine("Chapter 2 example 9

		// step 1
		Document document = new Document();

		// step 2
		// we create a writer that listens to the document
		// and directs a PDF-stream to a file
		PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap0209.pdf", FileMode.Create));

		// step 3
		document.open();

		// step 4
		// we create an Event and add it to the writer
		Glossary pageEvent = new Glossary();
		writer.setPageEvent(pageEvent);

		int i;
		// we add some content
		String[] f = new String[14];
		i = 0;
		f[i++] = "Courier";
		f[i++] = "Courier Bold";
		f[i++] = "Courier Italic";
		f[i++] = "Courier Bold Italic";
		f[i++] = "Helvetica";
		f[i++] = "Helvetica bold";
		f[i++] = "Helvetica italic";
		f[i++] = "Helvetica bold italic";
		f[i++] = "Times New Roman";
		f[i++] = "Times New Roman bold";
		f[i++] = "Times New Roman italic";
		f[i++] = "Times New Roman bold italic";
		f[i++] = "Symbol";
		f[i++] = "Zapfdingbats";

		Font[] fonts = new Font[f.Length];
		i = 0;
		fonts[i++] = FontFactory.getFont(FontFactory.COURIER, 12, Font.NORMAL);
		fonts[i++] = FontFactory.getFont(FontFactory.COURIER, 12, Font.BOLD);
		fonts[i++] = FontFactory.getFont(FontFactory.COURIER, 12, Font.ITALIC);
		fonts[i++] = FontFactory.getFont(FontFactory.COURIER, 12, Font.BOLD | Font.ITALIC);
		fonts[i++] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.NORMAL);
		fonts[i++] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.BOLD);
		fonts[i++] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.ITALIC);
		fonts[i++] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.BOLD | Font.ITALIC);
		fonts[i++] = FontFactory.getFont(FontFactory.TIMES_ROMAN, 12, Font.NORMAL);
		fonts[i++] = FontFactory.getFont(FontFactory.TIMES_ROMAN, 12, Font.BOLD);
		fonts[i++] = FontFactory.getFont(FontFactory.TIMES_ROMAN, 12, Font.ITALIC);
		fonts[i++] = FontFactory.getFont(FontFactory.TIMES_ROMAN, 12, Font.BOLD | Font.ITALIC);
		fonts[i++] = FontFactory.getFont(FontFactory.SYMBOL, 12, Font.NORMAL);
		fonts[i++] = FontFactory.getFont(FontFactory.ZAPFDINGBATS, 12, Font.NORMAL);

		for (i = 0; i < f.Length; i++) 
		{
			Chunk chunk = new Chunk("This is font ", fonts[i]);
			Paragraph p = new Paragraph(chunk);
			p.add(new Phrase(new Chunk(f[i], fonts[i]).setGenericTag(f[i])));
			document.add(p);
			if (i % 4 == 3) 
			{
				document.newPage();
			}
		}
		for (i = 0; i < f.Length; i++) 
		{
			Chunk chunk = new Chunk("This is font ", fonts[i]);
			Paragraph p = new Paragraph(chunk);
			p.add(new Phrase(new Chunk(f[i], fonts[i]).setGenericTag(f[i])));
			document.add(p);
			if (i % 4 == 3) 
			{
				document.newPage();
			}
		}

		// we add the glossary
		document.newPage();
		SortedList glossary = pageEvent.getGlossary();
		for (i = 0; i < glossary.Count; i++)
		{
			string key = (string)glossary.GetKey(i);
			int page = (int)glossary.GetByIndex(i);
			Paragraph g = new Paragraph(key);
			g.add(" 
			g.add(page.ToString());
			document.add(g);
		}
		for (i = 0; i < glossary.Count; i++)
		{
			string key = (string)glossary.GetKey(i);
			int page = (int)glossary.GetByIndex(i);
			Paragraph g = new Paragraph(key);
			g.add(" 
			g.add(page.ToString());
			document.add(g);
		}

		// step 5
		document.close();
	}

