		String text3 = "Some cyrillic characters
		document.add(new Paragraph(text1, font));
		document.add(new Paragraph(text2, font));
		document.add(new Paragraph(text3, font));

		// step 5
		document.close();
	}


public static void Main(string[] args) 
{
	Document doc;
	PdfWriter writer;
	string type;

	type = "PDF/X1a";
	Console.Out.WriteLine("Create " + type + " document.");
	doc = new Document();
	writer = PdfWriter.getInstance(doc, new FileStream("pdfx1a.pdf", FileMode.Create));
	writer.setPDFXConformance(PdfWriter.PDFX1A2001);
	Sub(doc, writer, type);
	doc.close();

	type = "PDF/X3";
	Console.Out.WriteLine("Create " + type + " document.");
	doc = new Document();
	writer = PdfWriter.getInstance(doc, new FileStream("pdfx3.pdf", FileMode.Create));
	writer.setPDFXConformance(PdfWriter.PDFX32002);
	Sub(doc, writer, type);
	doc.close();
}

