	writer.setPDFXConformance(PdfWriter.PDFX1A2001);
	Sub(doc, writer, type);
	doc.close();

	type = "PDF/X3";
	Console.Out.WriteLine("Create " + type + " document.");
	doc = new Document();
	writer = PdfWriter.getInstance(doc, new FileStream("pdfx3.pdf", FileMode.Create));
