//
// We use the tagfile from chapter 7, but we want to change some tag definitions.
//
class MyMap 
{
	public MyMap(String tagfile) 
	{
		XmlPeer peer = new XmlPeer(ElementTags.CHUNK, "SPEAKER");
		peer.addValue(MarkupTags.CSS_KEY_FONTSIZE, "10");
		peer.addValue(MarkupTags.CSS_KEY_FONTWEIGHT, MarkupTags.CSS_VALUE_BOLD);
		peer.addValue(ElementTags.GENERICTAG, "");
		put(peer.getAlias(), peer);
	}

{
	Console.WriteLine("Chapter 6 example 3

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file

	PdfWriter.getInstance(document, new FileStream("Chap0603.pdf", FileMode.Create));
	HtmlWriter writer = HtmlWriter.getInstance(document, new FileStream("Chap0603.html", FileMode.Create));

	writer.setImagepath("./");	//ujihara

	// step 3
	document.open();

	// step 4
	Image jpg = Image.getInstance("raf.jpg");
	jpg.scalePercent(50);
	document.add(jpg);

	// step 5
	document.close();
}

