//
// We use the tagfile from chapter 7, but we want to change some tag definitions.
//
class MyMap 
{
	public MyMap(String tagfile) 
	{
		XmlPeer peer = new XmlPeer(ElementTags.CHUNK, "SPEAKER");
		peer.addValue(MarkupTags.CSS_KEY_FONTSIZE, "10");
		peer.addValue(MarkupTags.CSS_KEY_FONTWEIGHT, MarkupTags.CSS_VALUE_BOLD);
		peer.addValue(ElementTags.GENERICTAG, "");
		put(peer.getAlias(), peer);
	}

{
	Console.WriteLine("Chapter 5 example 1
	// step 1
	Document document = new Document();
	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileStream("Chap0501.pdf", FileMode.Create));
	// step 3
	document.open();
	// step 4
	Table aTable = new Table(2,2);    // 2 rows, 2 columns
	aTable.addCell("0.0");
	aTable.addCell("0.1");
	aTable.addCell("1.0");
	aTable.addCell("1.1");
	document.add(aTable);           
	// step 5
	document.close();
}


// we override the onEndPage method
public override void onEndPage(PdfWriter writer, Document document) 
{
	int pageN = writer.getPageNumber();
	String text = "Page " + pageN + " of ";
	float len = bf.getWidthPoint(text, 8);
	cb.beginText();
	cb.setFontAndSize(bf, 8);
	cb.setTextMatrix(280, 30);
	cb.showText(text);
	cb.endText();
	cb.addTemplate(template, 280 + len, 30);
	cb.beginText();
	cb.setFontAndSize(bf, 8);
	cb.setTextMatrix(280, 820);
	if (pageN % 2 == 1) 
	{
		cb.showText("Romeo and Juliet");
	}
	else 
	{
		cb.showText(act);
	}
	cb.endText();
}


private void AddRadioKid(String caption, String onState, String state, PdfFormField radio, PdfAppearance tpOn, PdfAppearance tpOff)
{
	PdfFormField radio1 = PdfFormField.createEmpty(writer);
	radio1.setFlags(flags);
	radio1.setWidget(new Rectangle(PosX, PosY, PosX + Height, PosY + Height), PdfAnnotation.HIGHLIGHT_INVERT);
	radio1.setAppearanceState(state);
	radio1.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, "Off", tpOff);
	radio1.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, onState, tpOn);
	radio.addKid(radio1);
	cb.beginText();
	cb.setTextMatrix(PosX + Height*2, PosY + (Height-fontSize)/2 + (Height/2)*0.1f);
	cb.showText(caption);
	cb.endText();
}


	// we override the onGenericTag method
	public override void onGenericTag(PdfWriter writer, Document document, Rectangle rect, String text) 
	{
		speakers.add(new Speaker(text));
	}

	// we override the onOpenDocument method
	public override void onOpenDocument(PdfWriter writer, Document document) 
	{
		try 
		{
			bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
			cb = writer.getDirectContent();
			template = cb.createTemplate(50, 50);
		}
		catch(DocumentException de) 
		{
		}
		catch(IOException ioe) 
		{
		}
	}

	// we override the onChapter method
	public override void onChapter(PdfWriter writer,Document document,float paragraphPosition,Paragraph title) 
	{
		System.Text.StringBuilder buf = new System.Text.StringBuilder();
		for (Iterator i = title.getChunks().iterator(); i.hasNext(); ) 
		{
			Chunk chunk = (Chunk) i.next();
			buf.Append(chunk.content());
		}
		act = buf.ToString();
	}

	// we override the onEndPage method
	public override void onEndPage(PdfWriter writer, Document document) 
	{
		int pageN = writer.getPageNumber();
		String text = "Page " + pageN + " of ";
		float len = bf.getWidthPoint(text, 8);
		cb.beginText();
		cb.setFontAndSize(bf, 8);
		cb.setTextMatrix(280, 30);
		cb.showText(text);
		cb.endText();
		cb.addTemplate(template, 280 + len, 30);
		cb.beginText();
		cb.setFontAndSize(bf, 8);
		cb.setTextMatrix(280, 820);
		if (pageN % 2 == 1) 
		{
			cb.showText("Romeo and Juliet");
		}
		else 
		{
			cb.showText(act);
		}
		cb.endText();
	}

	// we override the onCloseDocument method
	public override void onCloseDocument(PdfWriter writer, Document document) 
	{
		template.beginText();
		template.setFontAndSize(bf, 8);
		template.showText((writer.getPageNumber() - 1).ToString());
		template.endText();
	}

	// we add a method to retrieve the glossary
	public TreeSet getSpeakers() 
	{
		return speakers;
	}
}

