	table.setBorderWidth(1);
	table.setBorderColor(GDI.Color.Blue);
	table.setPadding(5);
	table.setSpacing(5);
	Cell cell = new Cell("header");
	cell.setHeader(true);
	cell.setColspan(3);
	table.addCell(cell);
	cell = new Cell("example cell with colspan 1 and rowspan 2");
	cell.setRowspan(2);
	cell.setBorderColor(GDI.Color.Red);
	table.addCell(cell);
	table.addCell("1.1");
	table.addCell("2.1");
	table.addCell("1.2");
	table.addCell("2.2");
	table.addCell("cell test1");
	cell = new Cell("big cell");
	cell.setRowspan(2);
	cell.setColspan(2);
	cell.setBackgroundColor(GDI.Color.LightGray);
	table.addCell(cell);
	table.addCell("cell test2");
	document.add(new Paragraph("repeating the same table 10 times, but with different offsets
	document.add(table);
	document.add(new Paragraph("blah blah."));
	document.add(table);
	document.add(new Paragraph("we increase the offset."));
	table.setOffset(32);
	document.add(table);
	document.add(new Paragraph("blah blah."));
	document.add(table);
	document.add(new Paragraph("blah blah."));
	document.add(table);
	document.add(new Paragraph("we use an offset 0."));
	table.setOffset(0);
	document.add(table);
	document.add(new Paragraph("blah blah."));
	document.add(table);
	document.add(new Paragraph("blah blah."));
	document.add(table);
	document.add(new Paragraph("A negative offset."));
	table.setOffset(-16);
	document.add(table);
	document.add(new Paragraph("blah blah."));
	document.add(table);
	// step 5
	document.close();
}

