static PdfArray GetObjects(PdfDictionary dic, PdfName name)
{
	if (!dic.contains(name)) return null;
	PdfObject o = GetObject(dic, name);
	return o is PdfArray ? (PdfArray)o 
}


static PdfObject GetFirstObject(PdfDictionary dic)
{
	Set keys = dic.getKeys();
	Iterator it = keys.iterator();
	if (!it.hasNext()) return null;
	PdfName key = (PdfName)it.next();
	return GetObject(dic, key);
}

