{
	Console.WriteLine("Chapter 10 example 2

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap1002.pdf", FileMode.Create));

	// step 3
	document.open();

	// step 4
	PdfContentByte cb = writer.getDirectContent();

	// first we draw some lines to be able to visualize the text alignment functions
	cb.setLineWidth(0f);
	cb.moveTo(250, 500);
	cb.lineTo(250, 800);
	cb.moveTo(50, 700);
	cb.lineTo(400, 700);
	cb.moveTo(50, 650);
	cb.lineTo(400, 650);
	cb.moveTo(50, 600);
	cb.lineTo(400, 600);
	cb.stroke();

	// we tell the ContentByte we're ready to draw text
	cb.beginText();

	BaseFont bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
	cb.setFontAndSize(bf, 12);
	String text = "Sample text for alignment";
	// we show some text starting on some absolute position with a given alignment
	cb.showTextAligned(PdfContentByte.ALIGN_CENTER, text + " Center", 250, 700, 0);
	cb.showTextAligned(PdfContentByte.ALIGN_RIGHT, text + " Right", 250, 650, 0);
	cb.showTextAligned(PdfContentByte.ALIGN_LEFT, text + " Left", 250, 600, 0);

	// we draw some text on a certain position
	cb.setTextMatrix(100, 400);
	cb.showText("Text at position 100,400.");

	// we draw some rotated text on a certain position
	cb.setTextMatrix(0, 1, -1, 0, 100, 300);
	cb.showText("Text at position 100,300, rotated 90 degrees.");

	// we draw some mirrored, rotated text on a certain position
	cb.setTextMatrix(0, 1, 1, 0, 200, 200);
	cb.showText("Text at position 200,200, mirrored and rotated 90 degrees.");

	// we tell the contentByte, we've finished GDI text
	cb.endText();

	// step 5
	document.close();
}

{

	Console.WriteLine("Chapter 10 example 1

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap1001.pdf", FileMode.Create));

	// step 3
	document.open();

	// step 4
	PdfContentByte cb = writer.getDirectContent();

	// an example of a rectangle with a diagonal in very thick lines
	cb.setLineWidth(10f);
	// draw a rectangle
	cb.rectangle(100, 700, 100, 100);
	// add the diagonal
	cb.moveTo(100, 700);
	cb.lineTo(200, 800);
	// stroke the lines
	cb.stroke();

	// an example of some circles
	cb.setLineDash(3, 3, 0);
	cb.setRGBColorStrokeF(0f, 255f, 0f);
	cb.circle(150f, 500f, 100f);
	cb.stroke();

	cb.setLineWidth(5f);
	cb.resetRGBColorStroke();
	cb.circle(150f, 500f, 50f);
	cb.stroke();

	// example with colorfill
	cb.setRGBColorFillF(0f, 255f, 0f);
	cb.moveTo(100f, 200f);
	cb.lineTo(200f, 250f);
	cb.lineTo(400f, 150f);
	// because we change the fill color BEFORE we stroke the triangle
	// the color of the triangle will be red instead of green
	cb.setRGBColorFillF(255f, 0f, 0f);
	cb.closePathFillStroke();

	// step 5
	document.close();
}

