private void AddText()
{
	String text = "Fill here.";

	PdfFormField field = PdfFormField.createTextField(writer, false, false, 0);
	field.setFlags(flags);
	field.setWidget(new Rectangle(PosX, PosY, PosX + Width, PosY + Height), PdfAnnotation.HIGHLIGHT_INVERT);
	field.setFieldName("ATextField");
	field.setValueAsString(text);
	field.setDefaultValueAsString(text);
	//field.setMKBorderColor(BorderColor);
	//field.setMKBackgroundColor(BackColor);
	field.setBorderStyle(new PdfBorderDictionary(BorderWidth, PdfBorderDictionary.STYLE_SOLID));
	field.setPage();

	PdfAppearance tp = cb.createAppearance(Width, Height);
	PdfAppearance da = (PdfAppearance)tp.getDuplicate();

	//setup a default appearance string, DA entry.
	da.setFontAndSize(helv, fontSize);
	da.setColorFill(textColor);
	field.setDefaultAppearanceString(da);

	//setup an appearance dictionary, PA entry (optional).
	tp.beginVariableText();
	tp.saveState(); // q
	tp.rectangle(BorderWidth, BorderWidth, Width - BorderWidth*2, Height - BorderWidth*2);
	tp.clip(); //W
	tp.newPath(); //n
	tp.beginText();
	tp.setFontAndSize(helv, fontSize);
	tp.setColorFill(uneditedColor);
	tp.setTextMatrix(BorderWidth*2, (Height - fontSize)/2 + fontSize/8);
	tp.showText(text);
	tp.endText();
	tp.restoreState(); //Q
	tp.endVariableText();
	field.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, tp);

	writer.addAnnotation(field);
	GoNextLine();
}

