private void AddCheckBox()
{
	PdfFormField field = PdfFormField.createCheckBox(writer);
	field.setFlags(flags);
	PdfAppearance tpOff = cb.createAppearance(Height, Height);
	PdfAppearance tpOn = cb.createAppearance(Height, Height);
	tpOff.rectangle(Height*0.1f, Height*0.1f, Height*0.8f, Height*0.8f);
	tpOff.stroke();

	tpOn.setRGBColorFill(255, 128, 128);
	tpOn.rectangle(Height*0.1f, Height*0.1f, Height*0.8f, Height*0.8f);
	tpOn.fillStroke();
	tpOn.moveTo(Height*0.1f, Height*0.1f);
	tpOn.lineTo(Height*0.9f, Height*0.9f);
	tpOn.moveTo(Height*0.1f, Height*0.9f);
	tpOn.lineTo(Height*0.9f, Height*0.1f);
	tpOn.stroke();

	field.setWidget(new Rectangle(PosX, PosY, PosX+Height, PosY+Height), PdfAnnotation.HIGHLIGHT_INVERT);
	field.setFieldName("CheckBox");
	field.setValueAsName("Off");
	field.setAppearanceState("Off");
	field.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, "Off", tpOff);
	field.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, "On", tpOn);
	writer.addAnnotation(field);

	cb.beginText();
	cb.setTextMatrix(PosX + Height*2, PosY + (Height-fontSize)/2 + (Height/2)*0.1f);
	cb.showText("Check Box");
	cb.endText();

	GoNextLine();
}

