	aTable = new Table(4,4);    // 4 rows, 4 columns
	aTable.setAutoFillEmptyCells(true);
	aTable.addCell("2.2", new GDI.Point(2,2));
	aTable.addCell("3.3", new GDI.Point(3,3));
	aTable.addCell("2.1", new GDI.Point(2,1));
	aTable.addCell("1.3", new GDI.Point(1,3));
	document.add(aTable);
	document.newPage();

	aTable = new Table(4,4);    // 4 rows, 4 columns
	aTable.addCell("2.2", new GDI.Point(2,2));
	aTable.addCell("3.3", new GDI.Point(3,3));
	aTable.addCell("2.1", new GDI.Point(2,1));
	aTable.addCell("1.3", new GDI.Point(1,3));
	document.add(aTable);

	// step 5
	document.close();
}

