{
	Console.WriteLine("Add signature place.");

	Document doc = new Document();
	PdfWriter writer = PdfWriter.getInstance(doc, new FileStream("signature.pdf", FileMode.Create));

	PdfAcroForm acroForm = writer.getAcroForm();
	doc.open();
	doc.add(new Paragraph("Hello World"));
	acroForm.addSignature("mysig", 73, 705, 149, 759);
	doc.close();
}

