	// step 4
	Paragraph paragraph = new Paragraph();
	paragraph.setAlignment(ElementConst.ALIGN_JUSTIFIED);
	for (int i = 0; i < 20; i++) 
	{
		paragraph.add("Hello World, Hello Sun, Hello Moon, Hello Stars, Hello Sea, Hello Land, Hello People. ");
	}
	for (int i = 0; i < 20; i++) 
	{
		paragraph.add("Hello World, Hello Sun, Hello Moon, Hello Stars, Hello Sea, Hello Land, Hello People. ");
	}
	document.add(paragraph);

	// step 5
	document.close();
}

