{
	Console.WriteLine("Chapter 5 example 14
	// step 1
	Document document = new Document();
	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileStream("Chap0514.pdf", FileMode.Create));
	// step 3
	document.open();
	// step 4

	// simple example

	Table secondTable = new Table(2);
	secondTable.addCell("2nd table 0.0");
	secondTable.addCell("2nd table 0.1");
	secondTable.addCell("2nd table 1.0");
	secondTable.addCell("2nd table 1.1");

	Table aTable = new Table(4,4);    // 4 rows, 4 columns
	aTable.setAutoFillEmptyCells(true);
	aTable.addCell("2.2", new GDI.Point(2,2));
	aTable.addCell("3.3", new GDI.Point(3,3));
	aTable.addCell("2.1", new GDI.Point(2,1));
	aTable.insertTable(secondTable, new GDI.Point(1,3));
	document.add(aTable);

	// example with 2 nested tables

	Table thirdTable = new Table(2);
	thirdTable.addCell("3rd table 0.0");
	thirdTable.addCell("3rd table 0.1");
	thirdTable.addCell("3rd table 1.0");
	thirdTable.addCell("3rd table 1.1");

	aTable = new Table(5,5);
	aTable.setAutoFillEmptyCells(true);
	aTable.addCell("2.2", new GDI.Point(2,2));
	aTable.addCell("3.3", new GDI.Point(3,3));
	aTable.addCell("2.1", new GDI.Point(2,1));
	aTable.insertTable(secondTable, new GDI.Point(1,3));
	aTable.insertTable(thirdTable, new GDI.Point(6,2));
	document.add(aTable);        

	// relative column widths are preserved

	Table a = new Table( 2 );
	a.setWidths( new float[] { 85, 15 } );
	a.addCell("a-1");
	a.addCell("a-2");

	Table b = new Table(5);
	b.setWidths( new float[] { 15, 7, 7, 7, 7 } );
	b.addCell("b-1");
	b.addCell("b-2");
	b.addCell("b-3");
	b.addCell("b-4");
	b.addCell("b-5");

	// now, insert these 2 tables into a third for layout purposes
	Table c = new Table( 3, 1 );
	c.setWidth( 100.0f );
	c.setWidths( new float[] { 20, 2, 78 } );
	c.insertTable(a, new GDI.Point(0,0) );
	c.insertTable(b, new GDI.Point(0,2) );

	document.add(c);

	// adding extra cells after adding a table

	Table t1 = new Table(3);
	t1.addCell("1.1");
	t1.addCell("1.2");
	t1.addCell("1.3");
	// nested
	Table t2 = new Table(2);
	t2.addCell("2.1");
	t2.addCell("2.2");

	// now insert the nested
	t1.insertTable(t2);
	t1.addCell("new cell");    // correct row/column ?
	document.add(t1);       

	// deep nesting

	t1=new Table(2,2); 
	for (int i = 0; i < 4; i++) 
	{
		t1.addCell("t1");
	}
	for (int i = 0; i < 4; i++) 
	{
		t1.addCell("t1");
	}

	t2=new Table(3,3);
	for (int i = 0; i < 9; i++) 
	{
		if (i == 4) t2.insertTable(t1);
		else t2.addCell("t2"); 
	}
	for (int i = 0; i < 9; i++) 
	{
		if (i == 4) t2.insertTable(t1);
		else t2.addCell("t2"); 
	}

	Table t3=new Table(4,4);
	for (int i = 0; i < 16; i++) 
	{
		if (i == 10) t3.insertTable(t2);
		else t3.addCell("t3"); 
	}
	for (int i = 0; i < 16; i++) 
	{
		if (i == 10) t3.insertTable(t2);
		else t3.addCell("t3"); 
	}

	document.add(t3); 
	// step 5
	document.close();
}

	{
        Console.WriteLine("Chapter 10 example 11

		// step 1
		Rectangle rect = new Rectangle(PageSize.A4);
		rect.setBackgroundColor(GDI.Color.FromArgb(238, 221, 88));
		Document document = new Document(rect, 50, 50, 50, 50);
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap1011.pdf", FileMode.Create));
		// step 3
		document.open();
		// step 4
		PdfTemplate template = writer.getDirectContent().createTemplate(20, 20);
		BaseFont bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.WINANSI, BaseFont.NOT_EMBEDDED);
		String text = "Vertical";
		float size = 16;
		float width = bf.getWidthPoint(text, size);
		template.beginText();
		template.setRGBColorFillF(1, 1, 1);
		template.setFontAndSize(bf, size);
		template.setTextMatrix(0, 2);
		template.showText(text);
		template.endText();
		template.setWidth(width);
		template.setHeight(size + 2);
		Image img = Image.getInstance(template);
		img.setRotationDegrees(90);
		Chunk ck = new Chunk(img, 0, 0);
		PdfPTable table = new PdfPTable(3);
		table.setWidthPercentage(100);
		table.getDefaultCell().setHorizontalAlignment(ElementConst.ALIGN_CENTER);
		table.getDefaultCell().setVerticalAlignment(ElementConst.ALIGN_MIDDLE);
		PdfPCell cell = new PdfPCell(img);
		cell.setPadding(4);
		cell.setBackgroundColor(GDI.Color.Blue);
		cell.setHorizontalAlignment(ElementConst.ALIGN_CENTER);
		table.addCell("I see a template on my right");
		table.addCell(cell);
		table.addCell("I see a template on my left");
		table.addCell(cell);
		table.addCell("I see a template everywhere");
		table.addCell(cell);
		table.addCell("I see a template on my right");
		table.addCell(cell);
		table.addCell("I see a template on my left");

		Paragraph p1 = new Paragraph("This is a template ");
		p1.add(ck);
		p1.add(" just here.");
		p1.setLeading(img.scaledHeight() * 1.1f);
		document.add(p1);
		document.add(table);
		Paragraph p2 = new Paragraph("More templates ");
		p2.setLeading(img.scaledHeight() * 1.1f);
		p2.setAlignment(ElementConst.ALIGN_JUSTIFIED);
		img.scalePercent(70);
		for (int k = 0; k < 20; ++k)
			p2.add(ck);
		for (int k = 0; k < 20; ++k)
			p2.add(ck);
		document.add(p2);
		// step 5
		document.close();
	}

