	document.add(new Paragraph("blah blah."));
	document.add(table);
	document.add(new Paragraph("we increase the offset."));
	table.setOffset(32);
	document.add(table);
	document.add(new Paragraph("blah blah."));
	document.add(table);
	document.add(new Paragraph("blah blah."));
	document.add(table);
	document.add(new Paragraph("we use an offset 0."));
	table.setOffset(0);
	document.add(table);
	document.add(new Paragraph("blah blah."));
	document.add(table);
	document.add(new Paragraph("blah blah."));
	document.add(table);
	document.add(new Paragraph("A negative offset."));
	table.setOffset(-16);
	document.add(table);
	document.add(new Paragraph("blah blah."));
	document.add(table);
	// step 5
	document.close();
}

