	datatable.addCell("Company");
	datatable.addCell("Department");

	// This cell spans the remaining 6 columns in 1 row
	datatable.setDefaultRowspan(1);
	datatable.setDefaultColspan(6);
	datatable.addCell("Permissions");

	// These cells span 1 row and 1 column
	datatable.setDefaultColspan(1);
	datatable.addCell("Admin");
	datatable.addCell("Data");
	datatable.addCell("Expl");
	datatable.addCell("Prod");
	datatable.addCell("Proj");
	datatable.addCell("Online");

	datatable.setDefaultCellBorderWidth(1);
	datatable.setDefaultRowspan(1);

	return datatable;
}


private void AddCheckBox()
{
	PdfFormField field = PdfFormField.createCheckBox(writer);
	field.setFlags(flags);
	PdfAppearance tpOff = cb.createAppearance(Height, Height);
	PdfAppearance tpOn = cb.createAppearance(Height, Height);
	tpOff.rectangle(Height*0.1f, Height*0.1f, Height*0.8f, Height*0.8f);
	tpOff.stroke();

	tpOn.setRGBColorFill(255, 128, 128);
	tpOn.rectangle(Height*0.1f, Height*0.1f, Height*0.8f, Height*0.8f);
	tpOn.fillStroke();
	tpOn.moveTo(Height*0.1f, Height*0.1f);
	tpOn.lineTo(Height*0.9f, Height*0.9f);
	tpOn.moveTo(Height*0.1f, Height*0.9f);
	tpOn.lineTo(Height*0.9f, Height*0.1f);
	tpOn.stroke();

	field.setWidget(new Rectangle(PosX, PosY, PosX+Height, PosY+Height), PdfAnnotation.HIGHLIGHT_INVERT);
	field.setFieldName("CheckBox");
	field.setValueAsName("Off");
	field.setAppearanceState("Off");
	field.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, "Off", tpOff);
	field.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, "On", tpOn);
	writer.addAnnotation(field);

	cb.beginText();
	cb.setTextMatrix(PosX + Height*2, PosY + (Height-fontSize)/2 + (Height/2)*0.1f);
	cb.showText("Check Box");
	cb.endText();

	GoNextLine();
}


private void AddRadioKid(String caption, String onState, String state, PdfFormField radio, PdfAppearance tpOn, PdfAppearance tpOff)
{
	PdfFormField radio1 = PdfFormField.createEmpty(writer);
	radio1.setFlags(flags);
	radio1.setWidget(new Rectangle(PosX, PosY, PosX + Height, PosY + Height), PdfAnnotation.HIGHLIGHT_INVERT);
	radio1.setAppearanceState(state);
	radio1.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, "Off", tpOff);
	radio1.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, onState, tpOn);
	radio.addKid(radio1);
	cb.beginText();
	cb.setTextMatrix(PosX + Height*2, PosY + (Height-fontSize)/2 + (Height/2)*0.1f);
	cb.showText(caption);
	cb.endText();
}


