{
	Console.WriteLine("Chapter 5 example 7
	// step 1
	Document document = new Document();
	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileStream("Chap0507.pdf", FileMode.Create));
	// step 3
	document.open();
	// step 4
	Table table = new Table(3);
	table.setBorderWidth(1);
	table.setBorderColor(GDI.Color.Blue);
	table.setBorder(Rectangle.TOP | Rectangle.BOTTOM);
	table.setPadding(5);
	table.setSpacing(5);
	Cell cell = new Cell("header");
	cell.setHeader(true);
	cell.setBorderWidth(3);
	cell.setBorder(Rectangle.TOP | Rectangle.BOTTOM);
	cell.setColspan(3);
	table.addCell(cell);
	cell = new Cell("example cell with colspan 1 and rowspan 2");
	cell.setRowspan(2);
	cell.setBorderColor(GDI.Color.Red);
	cell.setBorder(Rectangle.LEFT | Rectangle.BOTTOM);
	table.addCell(cell);
	table.addCell("1.1");
	table.addCell("2.1");
	table.addCell("1.2");
	table.addCell("2.2");
	table.addCell("cell test1");
	cell = new Cell("big cell");
	cell.setRowspan(2);
	cell.setColspan(2);
	cell.setBorder(Rectangle.NO_BORDER);
	cell.setGrayFill(0.9f);
	table.addCell(cell);
	table.addCell("cell test2");
	document.add(table);
	// step 5
	document.close();
}

