	cell.setBorderColor(GDI.Color.Red);
	table.addCell(cell);
	table.addCell("1.1");
	table.addCell("2.1");
	table.addCell("1.2");
	table.addCell("2.2");
	table.addCell("cell test1");
	cell = new Cell("big cell");
	cell.setRowspan(2);
	cell.setColspan(2);
	cell.setBackgroundColor(GDI.Color.LightGray);
	table.addCell(cell);
	table.addCell("cell test2");
	document.add(new Paragraph("repeating the same table 10 times
	for (int i = 0; i < 10; i++) 
	{
		document.add(table);
	}
	for (int i = 0; i < 10; i++) 
	{
		document.add(table);
	}
	// step 5
	document.close();
}

