		// step 3
		document.open();

		// step 4
		BaseFont bfComic = BaseFont.createFont(
			(String)Microsoft.Win32.Registry.CurrentUser.
			OpenSubKey("Software").
			OpenSubKey("Microsoft").
			OpenSubKey("Windows").
			OpenSubKey("CurrentVersion").
			OpenSubKey("Explorer").
			OpenSubKey("Shell Folders").
			GetValue("Fonts") + java.io.File.separator + "comic.ttf", BaseFont.IDENTITY_H, BaseFont.EMBEDDED);
		Font font = new Font(bfComic, 12);
		String text1 = "This is the quite popular True Type font 'Comic'.";
		String text2 = "Some greek characters
		String text3 = "Some cyrillic characters
		document.add(new Paragraph(text1, font));
		document.add(new Paragraph(text2, font));
		document.add(new Paragraph(text3, font));

		// step 5
		document.close();
	}

