{
	Console.WriteLine("Chapter 5 example 16
	// step 1
	Document document = new Document(PageSize.A4.rotate());
	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileStream("Chap0516.pdf", FileMode.Create));
	// step 3
	document.open();
	// step 4
	Table secondTable = new Table(2);
	secondTable.addCell("2nd table 0.0");
	secondTable.addCell("2nd table 0.1");
	secondTable.addCell("2nd table 1.0");
	secondTable.addCell("2nd table 1.1");
	Cell tableCell = new Cell("This is a nested table");
	tableCell.add(secondTable);

	Table aTable = new Table(3,3);    // 3 rows, 3 columns
	aTable.addCell("0.0", new GDI.Point(0,0));
	aTable.addCell("0.1", new GDI.Point(0,1));
	aTable.addCell("0.2", new GDI.Point(0,2));
	aTable.addCell("0.0", new GDI.Point(1,0));
	aTable.addCell(tableCell, new GDI.Point(1,1));
	aTable.addCell("2.2", new GDI.Point(1,2));
	aTable.addCell("2.0", new GDI.Point(2,0));
	aTable.addCell("2.1", new GDI.Point(2,1));
	aTable.addCell("2.2", new GDI.Point(2,2));
	document.add(aTable);
	// step 5
	document.close();
}

