	PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap1109.pdf", FileMode.Create));
	// step 3
	document.open();
	// step 4
	// we add some content
	document.add(new Paragraph("Outline action example"));
	// we add the outline
	PdfContentByte cb = writer.getDirectContent();
	new PdfOutline(cb.getRootOutline(), new PdfAction(@"http
	new PdfOutline(cb.getRootOutline(), new PdfAction(@"http
	new PdfOutline(cb.getRootOutline(), new PdfAction(@"Chap1102b.pdf", 3), "Go to page 3 of Chap1102b.pdf");
	// step 5
	document.close();
}

