	PdfWriter.getInstance(documentB, new FileStream("Chap0704b.pdf", FileMode.Create));

	// step 3
	XmlParser.parse(documentA, "Chap0701.xml");
	XmlParser.parse(documentB, "Chap0703.xml", "tagmap0703.xml");
}

