	table.addCell("1.1");
	table.addCell("2.1");
	table.addCell("1.2");
	table.addCell("2.2");
	table.addCell("cell test1");
	cell = new Cell("big cell");
	cell.setRowspan(2);
	cell.setColspan(2);
	cell.setHorizontalAlignment(ElementConst.ALIGN_CENTER);
	cell.setVerticalAlignment(ElementConst.ALIGN_MIDDLE);
	cell.setBackgroundColor(GDI.Color.LightGray);
