	// we trigger a page break
	document.newPage();

	// PAGE 3
	// we add some more content
	document.add(new Paragraph("Hello Sun"));
	document.add(new Paragraph("Remark
	// we reset the page numbering
	document.resetPageCount();
	// we trigger a page break
	document.newPage();

