{
	try 
	{
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);
		PdfWriter writer = PdfWriter.getInstance(document, new FileStream("arabic_hebrew.pdf", FileMode.Create));

		document.open();
		PdfContentByte cb = writer.getDirectContent();
		BaseFont bf = BaseFont.createFont(
			(String)Microsoft.Win32.Registry.CurrentUser.
			OpenSubKey("Software").
			OpenSubKey("Microsoft").
			OpenSubKey("Windows").
			OpenSubKey("CurrentVersion").
			OpenSubKey("Explorer").
			OpenSubKey("Shell Folders").
			GetValue("Fonts")
			+ java.io.File.separator + "times.ttf", BaseFont.IDENTITY_H, true);
		Font f2 = new Font(bf, 24, Font.NORMAL, GDI.Color.Blue);
		Phrase ph = new Phrase();
		float llx = 100;
		float lly = 100;
		float urx = 500;
		float ury = 800;
		ColumnText ct = new ColumnText(cb);
		ct.setSimpleColumn(llx, lly, urx, ury, 24, ElementConst.ALIGN_LEFT);
		ct.setSpaceCharRatio(PdfWriter.NO_SPACE_CHAR_RATIO);
		ct.setLeading(0, 1);
		ct.setRunDirection(PdfWriter.RUN_DIRECTION_RTL);
		ct.setAlignment(ElementConst.ALIGN_CENTER);
		ct.addText(new Chunk(ar1, new Font(bf, 16)));
		ct.addText(new Chunk(ar2, new Font(bf, 16, Font.NORMAL, GDI.Color.Red)));
		ct.go();
		ct.setAlignment(ElementConst.ALIGN_JUSTIFIED);
		ct.addText(new Chunk(ar3, new Font(bf, 12)));
		ct.go();
		ct.setAlignment(ElementConst.ALIGN_CENTER);
		ct.addText(new Chunk(ar4, new Font(bf, 14)));
		ct.go();

		ct.setSpaceCharRatio(PdfWriter.SPACE_CHAR_RATIO_DEFAULT);
		ct.setAlignment(ElementConst.ALIGN_CENTER);
		ct.addText(new Chunk("\n\n\n", new Font(bf, 16)));
		ct.addText(new Chunk(he1, new Font(bf, 16)));
		ct.addText(new Chunk(he2, new Font(bf, 16, Font.NORMAL, GDI.Color.Red)));
		ct.go();
		ct.setAlignment(ElementConst.ALIGN_JUSTIFIED);
		ct.addText(new Chunk(he3, new Font(bf, 12)));
		ct.go();
		ct.setAlignment(ElementConst.ALIGN_CENTER);
		ct.addText(new Chunk(he4, new Font(bf, 14)));
		ct.go();

		//            cb.setLineWidth(0);
		//            cb.setLineDash(2, 6, 0);
		//            cb.rectangle(llx, lly, urx - llx, ury - lly);
		//            cb.stroke();

		document.newPage();
		String atext = "\u062a\u0635\u0628\u062d ";
		PdfPTable table = new PdfPTable(5);
		table.setWidthPercentage(100);
		table.setRunDirection(PdfWriter.RUN_DIRECTION_NO_BIDI);
		for (int k = 0; k < 5; ++k) 
		{
			PdfPCell cell = new PdfPCell(new Phrase(10, atext + k, f2));
			if (k == 2) 
			{
				cell.setColspan(2);
				++k;
			}
			table.addCell(cell);
		}
		for (int k = 0; k < 5; ++k) 
		{
			PdfPCell cell = new PdfPCell(new Phrase(10, atext + k, f2));
			if (k == 2) 
			{
				cell.setColspan(2);
				++k;
			}
			table.addCell(cell);
		}
		table.setRunDirection(PdfWriter.RUN_DIRECTION_LTR);
		for (int k = 0; k < 5; ++k) 
		{
			PdfPCell cell = new PdfPCell(new Phrase(10, atext + k, f2));
			if (k == 2) 
			{
				cell.setColspan(2);
				++k;
			}
			table.addCell(cell);
		}
		for (int k = 0; k < 5; ++k) 
		{
			PdfPCell cell = new PdfPCell(new Phrase(10, atext + k, f2));
			if (k == 2) 
			{
				cell.setColspan(2);
				++k;
			}
			table.addCell(cell);
		}
		table.setRunDirection(PdfWriter.RUN_DIRECTION_RTL);
		for (int k = 0; k < 5; ++k) 
		{
			PdfPCell cell = new PdfPCell(new Phrase(10, atext + k, f2));
			if (k == 2) 
			{
				cell.setColspan(2);
				++k;
			}
			table.addCell(cell);
		}
		for (int k = 0; k < 5; ++k) 
		{
			PdfPCell cell = new PdfPCell(new Phrase(10, atext + k, f2));
			if (k == 2) 
			{
				cell.setColspan(2);
				++k;
			}
			table.addCell(cell);
		}
		document.add(table);
		document.close();

		Console.WriteLine("finished");
	}
	catch (Exception e) 
	{
		Console.WriteLine(e.StackTrace);
	}
}

{
	Console.WriteLine("Chapter 5 example 14
	// step 1
	Document document = new Document();
	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileStream("Chap0514.pdf", FileMode.Create));
	// step 3
	document.open();
	// step 4

	// simple example

	Table secondTable = new Table(2);
	secondTable.addCell("2nd table 0.0");
	secondTable.addCell("2nd table 0.1");
	secondTable.addCell("2nd table 1.0");
	secondTable.addCell("2nd table 1.1");

	Table aTable = new Table(4,4);    // 4 rows, 4 columns
	aTable.setAutoFillEmptyCells(true);
	aTable.addCell("2.2", new GDI.Point(2,2));
	aTable.addCell("3.3", new GDI.Point(3,3));
	aTable.addCell("2.1", new GDI.Point(2,1));
	aTable.insertTable(secondTable, new GDI.Point(1,3));
	document.add(aTable);

	// example with 2 nested tables

	Table thirdTable = new Table(2);
	thirdTable.addCell("3rd table 0.0");
	thirdTable.addCell("3rd table 0.1");
	thirdTable.addCell("3rd table 1.0");
	thirdTable.addCell("3rd table 1.1");

	aTable = new Table(5,5);
	aTable.setAutoFillEmptyCells(true);
	aTable.addCell("2.2", new GDI.Point(2,2));
	aTable.addCell("3.3", new GDI.Point(3,3));
	aTable.addCell("2.1", new GDI.Point(2,1));
	aTable.insertTable(secondTable, new GDI.Point(1,3));
	aTable.insertTable(thirdTable, new GDI.Point(6,2));
	document.add(aTable);        

	// relative column widths are preserved

	Table a = new Table( 2 );
	a.setWidths( new float[] { 85, 15 } );
	a.addCell("a-1");
	a.addCell("a-2");

	Table b = new Table(5);
	b.setWidths( new float[] { 15, 7, 7, 7, 7 } );
	b.addCell("b-1");
	b.addCell("b-2");
	b.addCell("b-3");
	b.addCell("b-4");
	b.addCell("b-5");

	// now, insert these 2 tables into a third for layout purposes
	Table c = new Table( 3, 1 );
	c.setWidth( 100.0f );
	c.setWidths( new float[] { 20, 2, 78 } );
	c.insertTable(a, new GDI.Point(0,0) );
	c.insertTable(b, new GDI.Point(0,2) );

	document.add(c);

	// adding extra cells after adding a table

	Table t1 = new Table(3);
	t1.addCell("1.1");
	t1.addCell("1.2");
	t1.addCell("1.3");
	// nested
	Table t2 = new Table(2);
	t2.addCell("2.1");
	t2.addCell("2.2");

	// now insert the nested
	t1.insertTable(t2);
	t1.addCell("new cell");    // correct row/column ?
	document.add(t1);       

	// deep nesting

	t1=new Table(2,2); 
	for (int i = 0; i < 4; i++) 
	{
		t1.addCell("t1");
	}
	for (int i = 0; i < 4; i++) 
	{
		t1.addCell("t1");
	}

	t2=new Table(3,3);
	for (int i = 0; i < 9; i++) 
	{
		if (i == 4) t2.insertTable(t1);
		else t2.addCell("t2"); 
	}
	for (int i = 0; i < 9; i++) 
	{
		if (i == 4) t2.insertTable(t1);
		else t2.addCell("t2"); 
	}

	Table t3=new Table(4,4);
	for (int i = 0; i < 16; i++) 
	{
		if (i == 10) t3.insertTable(t2);
		else t3.addCell("t3"); 
	}
	for (int i = 0; i < 16; i++) 
	{
		if (i == 10) t3.insertTable(t2);
		else t3.addCell("t3"); 
	}

	document.add(t3); 
	// step 5
	document.close();
}

