{
	Console.WriteLine("Chapter 8 example 2

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	RtfWriter.getInstance(document, new FileStream("Chap0802.rtf", FileMode.Create));

	// step 3
	document.open();

	// step 4
	Paragraph paragraph0 = new Paragraph();
	Paragraph paragraph1 = new Paragraph("(1) this is a Paragraph");
	// When a Font is passed (explicitely or embedded in a chunk),
	// the default leading = 1.5 * size of the font
	Paragraph paragraph3 = new Paragraph("(3) this is a Paragraph with a red, normal font Courier, size 20.", new Font(Font.COURIER, 20, Font.NORMAL, GDI.Color.Red));
	Paragraph paragraph4 = new Paragraph(new Chunk("(4) this is a Paragraph"));
	Paragraph paragraph5 = new Paragraph(new Chunk("(5) this is a Paragraph in Helvetica, bold, red and size 16.", new Font(Font.HELVETICA, 16, Font.BOLD, GDI.Color.Red)));
	// A Paragraph can contains several chunks with different fonts
	Paragraph paragraph6 = new Paragraph("(6)");
	Chunk chunk = new Chunk(" This is a font
	paragraph6.add(chunk);
	paragraph6.add(new Chunk("Helvetica", new Font(Font.HELVETICA)));
	paragraph6.add(chunk);
	paragraph6.add(new Chunk("Times New Roman", new Font(Font.TIMES_ROMAN)));
	paragraph6.add(chunk);
	paragraph6.add(new Chunk("Courier", new Font(Font.COURIER)));
	paragraph6.add(chunk);
	paragraph6.add(new Chunk("Symbol", new Font(Font.SYMBOL)));
	paragraph6.add(chunk);
	paragraph6.add(new Chunk("ZapfDingBats", new Font(Font.ZAPFDINGBATS)));
	Anchor anchor1 = new Anchor("website (external reference)", new Font(Font.HELVETICA, 12, Font.UNDERLINE, GDI.Color.Blue));
	anchor1.setReference("http
	document.add(paragraph1);
	document.add(paragraph3);
	document.add(paragraph4);
	document.add(paragraph5);
	document.add(paragraph6);
	document.add(anchor1);
	// step 5
	document.close();
}

{
	Console.WriteLine("Chapter 6 example 15
	// step 1
	Document document = new Document();
	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileStream("Chap0615.pdf", FileMode.Create));
	// step 3
	document.open();
	// step 4
	Image img0 = Image.getInstance("myKids.jpg");
	img0.setAlignment(Image.MIDDLE);
	Image img1 = Image.getInstance("pngnow.png");
	img1.setAlignment(Image.LEFT | Image.UNDERLYING);
	Image img2 = Image.getInstance("pngnow.png");
	img2.setAlignment(Image.RIGHT | Image.TEXTWRAP);
	Image img3 = Image.getInstance("pngnow.png");
	img3.setAlignment(Image.LEFT);
	Image img4 = Image.getInstance("pngnow.png");
	img4.setAlignment(Image.MIDDLE);
	Image img5 = Image.getInstance("pngnow.png");
	img5.setAlignment(Image.RIGHT);
	Table table = new Table(3);
	table.setPadding(2);
	table.setDefaultHorizontalAlignment(ElementConst.ALIGN_CENTER);
	// row 1
	table.addCell("I see an image\non my right");
	Cell cell = new Cell("This is the image (aligned in the middle)
	cell.setBackgroundColor(GDI.Color.LightGray);
	cell.add(img0);
	cell.add(new Phrase("This was the image"));
	table.addCell(cell);
	table.addCell("I see an image\non my left");
	// row 2
	cell = new Cell("This is the image (left aligned)
	cell.add(img1);
	cell.add(new Phrase("This was the image"));
	table.addCell(cell);
	table.addCell("I see images\neverywhere");
	cell = new Cell("This is the image (right aligned)
	cell.add(img2);
	cell.add(new Phrase("This was the image"));
	table.addCell(cell);
	// row 3
	table.addCell("I see an image\non my right");
	cell = new Cell(img3);
	cell.add(img4);
	cell.add(img5);
	table.addCell(cell);
	table.addCell("I see an image\non my left");
	document.add(table);

	// step 5
	document.close();
}

