{
	Console.WriteLine("Chapter 6 example 15
	// step 1
	Document document = new Document();
	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileStream("Chap0615.pdf", FileMode.Create));
	// step 3
	document.open();
	// step 4
	Image img0 = Image.getInstance("myKids.jpg");
	img0.setAlignment(Image.MIDDLE);
	Image img1 = Image.getInstance("pngnow.png");
	img1.setAlignment(Image.LEFT | Image.UNDERLYING);
	Image img2 = Image.getInstance("pngnow.png");
	img2.setAlignment(Image.RIGHT | Image.TEXTWRAP);
	Image img3 = Image.getInstance("pngnow.png");
	img3.setAlignment(Image.LEFT);
	Image img4 = Image.getInstance("pngnow.png");
	img4.setAlignment(Image.MIDDLE);
	Image img5 = Image.getInstance("pngnow.png");
	img5.setAlignment(Image.RIGHT);
	Table table = new Table(3);
	table.setPadding(2);
	table.setDefaultHorizontalAlignment(ElementConst.ALIGN_CENTER);
	// row 1
	table.addCell("I see an image\non my right");
	Cell cell = new Cell("This is the image (aligned in the middle)
	cell.setBackgroundColor(GDI.Color.LightGray);
	cell.add(img0);
	cell.add(new Phrase("This was the image"));
	table.addCell(cell);
	table.addCell("I see an image\non my left");
	// row 2
	cell = new Cell("This is the image (left aligned)
	cell.add(img1);
	cell.add(new Phrase("This was the image"));
	table.addCell(cell);
	table.addCell("I see images\neverywhere");
	cell = new Cell("This is the image (right aligned)
	cell.add(img2);
	cell.add(new Phrase("This was the image"));
	table.addCell(cell);
	// row 3
	table.addCell("I see an image\non my right");
	cell = new Cell(img3);
	cell.add(img4);
	cell.add(img5);
	table.addCell(cell);
	table.addCell("I see an image\non my left");
	document.add(table);

	// step 5
	document.close();
}

{
	Console.WriteLine("Chapter 5 example 16
	// step 1
	Document document = new Document(PageSize.A4.rotate());
	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileStream("Chap0516.pdf", FileMode.Create));
	// step 3
	document.open();
	// step 4
	Table secondTable = new Table(2);
	secondTable.addCell("2nd table 0.0");
	secondTable.addCell("2nd table 0.1");
	secondTable.addCell("2nd table 1.0");
	secondTable.addCell("2nd table 1.1");
	Cell tableCell = new Cell("This is a nested table");
	tableCell.add(secondTable);

	Table aTable = new Table(3,3);    // 3 rows, 3 columns
	aTable.addCell("0.0", new GDI.Point(0,0));
	aTable.addCell("0.1", new GDI.Point(0,1));
	aTable.addCell("0.2", new GDI.Point(0,2));
	aTable.addCell("0.0", new GDI.Point(1,0));
	aTable.addCell(tableCell, new GDI.Point(1,1));
	aTable.addCell("2.2", new GDI.Point(1,2));
	aTable.addCell("2.0", new GDI.Point(2,0));
	aTable.addCell("2.1", new GDI.Point(2,1));
	aTable.addCell("2.2", new GDI.Point(2,2));
	document.add(aTable);
	// step 5
	document.close();
}

