			datatable.addCell("No");
			datatable.addCell("Yes");
			datatable.addCell("No");
			datatable.addCell("Yes");
			datatable.addCell("No");
			datatable.addCell("Yes");

			if (!writer.fitsPage(datatable)) 
			{
				datatable.deleteLastRow();
				i--;
				document.add(datatable);
				document.newPage();
				datatable = getTable();
			}
        }
