{
	Console.WriteLine("Chapter 5 example 4
	// step 1
	Document document = new Document();
	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileStream("Chap0504.pdf", FileMode.Create));
	// step 3
	document.open();
	// step 4
	Table aTable = new Table(2,2);    // 2 rows, 2 columns
	aTable.setAutoFillEmptyCells(true);
	aTable.addCell("0.0");
	aTable.addCell("0.1");
	aTable.addCell("1.0");
	aTable.addCell("1.1");
	aTable.addColumns(2);
	float[] f = {1f, 1f, 1f, 1f};
	aTable.setWidths(f);
	aTable.addCell("2.2", new GDI.Point(2,2));
	aTable.addCell("3.3", new GDI.Point(3,3));
	aTable.addCell("2.1", new GDI.Point(2,1));
	aTable.addCell("1.3", new GDI.Point(1,3));
	aTable.addCell("5.2", new GDI.Point(5,2));
	aTable.addCell("6.1", new GDI.Point(6,1));
	aTable.addCell("5.0", new GDI.Point(5,0)); 
	document.add(aTable);  
	// step 5
	document.close();
}

