{
	Console.WriteLine("Chapter 5 example 2
	// step 1
	Document document = new Document();
	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileStream("Chap0502.pdf", FileMode.Create));
	// step 3
	document.open();
	// step 4
	Table aTable;

	aTable = new Table(4,4);    // 4 rows, 4 columns
	aTable.setAutoFillEmptyCells(true);
	aTable.addCell("2.2", new GDI.Point(2,2));
	aTable.addCell("3.3", new GDI.Point(3,3));
	aTable.addCell("2.1", new GDI.Point(2,1));
	aTable.addCell("1.3", new GDI.Point(1,3));
	document.add(aTable);
	document.newPage();

	aTable = new Table(4,4);    // 4 rows, 4 columns
	aTable.addCell("2.2", new GDI.Point(2,2));
	aTable.addCell("3.3", new GDI.Point(3,3));
	aTable.addCell("2.1", new GDI.Point(2,1));
	aTable.addCell("1.3", new GDI.Point(1,3));
	document.add(aTable);

	// step 5
	document.close();
}

