	document.add(g);
	document.add(new Paragraph("Hello World"));
	document.add(new Paragraph("Hello World\n\n"));
	g = new Graphic();
	g.setHorizontalLine(2f, 80f, GDI.Color.Red);
	document.add(g);
	document.add(new Paragraph("Hello World"));

	// step 5
	document.close();
