{
	Console.WriteLine("Chapter 6 example 10

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap0610.pdf", FileMode.Create));

	// step 3
	document.open();

	// step 4
	for (int i = 0; i < 300; i++) 
	{
		document.add(new Phrase("Who is this? "));
	}
	for (int i = 0; i < 300; i++) 
	{
		document.add(new Phrase("Who is this? "));
	}
	PdfContentByte cb = writer.getDirectContent();

	Image image = Image.getInstance(new System.Drawing.Bitmap("H.gif"));
	image.setAbsolutePosition(100, 200);
	cb.addImage(image);

	// step 5
	document.close();
}

