		// step 3
		document.open();

		// step 4
		PdfAction action = PdfAction.gotoLocalPage(2, new PdfDestination(PdfDestination.XYZ, -1, 10000, 0), writer);
		writer.setOpenAction(action);
		document.add(new Paragraph("Page 1"));
		document.newPage();
		document.add(new Paragraph("Page 2"));

		// step 5
		document.close();
	}

