	aTable.addCell("0.0", new GDI.Point(0,0));
	aTable.addCell("0.1", new GDI.Point(0,1));
	aTable.addCell("0.2", new GDI.Point(0,2));
	aTable.addCell("0.0", new GDI.Point(1,0));
	aTable.addCell(tableCell, new GDI.Point(1,1));
	aTable.addCell("2.2", new GDI.Point(1,2));
	aTable.addCell("2.0", new GDI.Point(2,0));
	aTable.addCell("2.1", new GDI.Point(2,1));
	aTable.addCell("2.2", new GDI.Point(2,2));
	document.add(aTable);
	// step 5
	document.close();
}


	// we override only the onParagraph method
	public override void onParagraph(PdfWriter writer, Document document, float position) 
	{
		n++;
		PdfContentByte cb = writer.getDirectContent();
		PdfDestination destination = new PdfDestination(PdfDestination.FITH, position);
		PdfOutline outline = new PdfOutline(cb.getRootOutline(), destination, "paragraph " + n);
		cb.addOutline(outline, outline.getTitle());
	}
}

