	// step 3
	document.open();

	// step 4
	Graphic grx = new Graphic();
	// add a rectangle
	grx.rectangle(100, 700, 100, 100);
	// add the diagonal
	grx.moveTo(100, 700);
	grx.lineTo(200, 800);
	// stroke the lines
	grx.stroke();
	document.add(grx);

	// step 5
	document.close();
}


