	document.open();
	// step 4
	Table aTable = new Table(2,2);    // 2 rows, 2 columns
	aTable.addCell("0.0");
	aTable.addCell("0.1");
	aTable.addCell("1.0");
	aTable.addCell("1.1");
	document.add(aTable);           
	// step 5
	document.close();
}

