{
	Console.WriteLine("Chapter 10 example 6

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap1006.pdf", FileMode.Create));

	// step 3
	document.open();

	// step 4

	// we grab the ContentByte and do some stuff with it
	PdfContentByte cb = writer.getDirectContent();

	BaseFont bf = BaseFont.createFont(BaseFont.COURIER, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
	Font font = new Font(bf, 11, Font.NORMAL);

	ColumnText ct = new ColumnText(cb);
	ct.setSimpleColumn(60, 300, 100, 300 + 28 * 15, 15, ElementConst.ALIGN_CENTER);
	ct.addText(new Phrase(15, "UNI\n", font));
	for (int i = 0; i < 27; i++) 
	{
		ct.addText(new Phrase(15, uni[i] + "\n", font));
	}
	for (int i = 0; i < 27; i++) 
	{
		ct.addText(new Phrase(15, uni[i] + "\n", font));
	}
	ct.go();
	cb.rectangle(103, 295, 52, 8 + 28 * 15);
	cb.stroke();
	ct.setSimpleColumn(105, 300, 150, 300 + 28 * 15, 15, ElementConst.ALIGN_RIGHT);
	ct.addText(new Phrase(15, "char\n", font));
	for (int i = 0; i < 27; i++) 
	{
		ct.addText(new Phrase(15, code[i] + "\n", font));
	}
	for (int i = 0; i < 27; i++) 
	{
		ct.addText(new Phrase(15, code[i] + "\n", font));
	}
	ct.go();
	ct.setSimpleColumn(160, 300, 500, 300 + 28 * 15, 15, ElementConst.ALIGN_LEFT);
	ct.addText(new Phrase(15, "NAME\n", font));
	for (int i = 0; i < 27; i++) 
	{
		ct.addText(new Phrase(15, name[i] + "\n", font));
	}
	for (int i = 0; i < 27; i++) 
	{
		ct.addText(new Phrase(15, name[i] + "\n", font));
	}
	ct.go();

	// step 5
	document.close();
}


	public void tableLayout(PdfPTable table, float[][] width, float[] heights, int headerRows, int rowStart, PdfContentByte[] canvases) 
	{
		float[] widths = width[0];
		PdfContentByte cb = canvases[PdfPTable.TEXTCANVAS];
		cb.saveState();
		cb.setLineWidth(2);
		cb.setRGBColorStroke(255, 0, 0);
		cb.rectangle(widths[0], heights[heights.Length - 1], widths[widths.Length - 1] - widths[0], heights[0] - heights[heights.Length - 1]);
		cb.stroke();
		if (headerRows > 0) 
		{
			float headerHeight = heights[0];
			for (int k = 0; k < headerRows; ++k)
				headerHeight += heights[k];
			cb.setRGBColorStroke(0, 0, 255);
			cb.rectangle(widths[0], heights[headerRows], widths[widths.Length - 1] - widths[0], heights[0] - heights[headerRows]);
			cb.stroke();
		}
		cb.restoreState();
		cb = canvases[PdfPTable.BASECANVAS];
		cb.saveState();
		cb.setLineWidth(.5f);
		for (int line = 0; line < heights.Length - 1; ++line) 
		{
			widths = width[line];
			for (int col = 0; col < widths.Length - 1; ++col) 
			{
				if (line == 0 && col == 0)
					cb.setAction(new PdfAction("http
						widths[col], heights[line + 1], widths[col + 1], heights[line]);
				cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
				cb.moveTo(widths[col], heights[line]);
				cb.lineTo(widths[col + 1], heights[line]);
				cb.stroke();
				cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
				cb.moveTo(widths[col], heights[line]);
				cb.lineTo(widths[col], heights[line + 1]);
				cb.stroke();
			}
		}
		cb.restoreState();
	}
}

public class Chap1203 
{
	public static void Main(String[] args) 
	{

		Console.WriteLine("Chapter 12 example 3

		// step 1
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap1203.pdf", FileMode.Create));
		// step 3
		document.open();
		// step 4
		BaseFont bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.WINANSI, BaseFont.NOT_EMBEDDED);
		// table 1
		PdfPTable table = new PdfPTable(4);
		table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
		for (int k = 0; k < 24; ++k) 
		{
			if (k != 0)
				table.addCell("" + k);
			else 
			{
				table.getDefaultCell().setColspan(3);
				table.getDefaultCell().setHorizontalAlignment(ElementConst.ALIGN_CENTER);
				table.addCell("This is a very big URL");
				table.getDefaultCell().setColspan(1);
				table.getDefaultCell().setHorizontalAlignment(ElementConst.ALIGN_LEFT);
				k += 2;
			}
		}
		for (int k = 0; k < 24; ++k) 
		{
			if (k != 0)
				table.addCell("" + k);
			else 
			{
				table.getDefaultCell().setColspan(3);
				table.getDefaultCell().setHorizontalAlignment(ElementConst.ALIGN_CENTER);
				table.addCell("This is a very big URL");
				table.getDefaultCell().setColspan(1);
				table.getDefaultCell().setHorizontalAlignment(ElementConst.ALIGN_LEFT);
				k += 2;
			}
		}
		MyTableEventColspan aevent = new MyTableEventColspan();
		table.setTableEvent(aevent);
		table.setTotalWidth(300);
		// write table 1 at some position
		table.writeSelectedRows(0, -1, 100, 600, writer.getDirectContent());
		// add table 1 (default position)
		document.add(table);
		document.newPage();
		// table 2
		table = new PdfPTable(4);
		float fontSize = 12;
		table.getDefaultCell().setPaddingTop(bf.getFontDescriptor(BaseFont.ASCENT, fontSize) - fontSize + 2);
		table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
		for (int k = 0; k < 500 * 4; ++k) 
		{
			if (k == 0) 
			{
				table.getDefaultCell().setColspan(4);
				table.getDefaultCell().setHorizontalAlignment(ElementConst.ALIGN_CENTER);
				table.addCell(new Phrase("This is an URL", new Font(bf, fontSize * 2)));
				table.getDefaultCell().setColspan(1);
				table.getDefaultCell().setHorizontalAlignment(ElementConst.ALIGN_LEFT);
				k += 3;
			}
			else
				table.addCell(new Phrase("" + k, new Font(bf, fontSize)));
		}
		for (int k = 0; k < 500 * 4; ++k) 
		{
			if (k == 0) 
			{
				table.getDefaultCell().setColspan(4);
				table.getDefaultCell().setHorizontalAlignment(ElementConst.ALIGN_CENTER);
				table.addCell(new Phrase("This is an URL", new Font(bf, fontSize * 2)));
				table.getDefaultCell().setColspan(1);
				table.getDefaultCell().setHorizontalAlignment(ElementConst.ALIGN_LEFT);
				k += 3;
			}
			else
				table.addCell(new Phrase("" + k, new Font(bf, fontSize)));
		}
		table.setTableEvent(aevent);
		table.setHeaderRows(3);
		document.add(table);
		// step 5
		document.close();
	}

